import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const MerchantDashboard = () => {
  const [profile, setProfile] = useState({
    name: "",
    email: "",
    phone: "",
    businessName: "",
    location: "",
    description: "",
    hours: "",
  });

  const [services, setServices] = useState([]);
  const [newService, setNewService] = useState({
    name: "",
    description: "",
    price: "",
    slots: "",
  });

  const [loading, setLoading] = useState(true);
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("authToken");
    if (!token) {
      navigate("/login");
      return;
    }

    const fetchMerchantData = async () => {
      try {
        // Fetch merchant profile
        const profileResponse = await axios.get("http://localhost:5000/api/merchants/profile", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setProfile(profileResponse.data);

        // Fetch merchant services
        const servicesResponse = await axios.get("http://localhost:5000/api/merchants/services", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setServices(servicesResponse.data);
      } catch (error) {
        console.error("Error fetching data:", error);
        setErrorMessage("Error loading merchant dashboard.");
      } finally {
        setLoading(false);
      }
    };

    fetchMerchantData();
  }, [navigate]);

  const updateProfile = async () => {
    const token = localStorage.getItem("authToken");
    try {
      await axios.put("http://localhost:5000/api/merchants/profile", profile, {
        headers: { Authorization: `Bearer ${token}` },
      });
      alert("Profile updated successfully");
    } catch (error) {
      console.error("Error updating profile:", error);
    }
  };

  const addService = async () => {
    const token = localStorage.getItem("authToken");
    try {
      await axios.post("http://localhost:5000/api/merchants/services", newService, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setNewService({ name: "", description: "", price: "", slots: "" });
      // Refresh services list
      const servicesResponse = await axios.get("http://localhost:5000/api/merchants/services", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setServices(servicesResponse.data);
    } catch (error) {
      console.error("Error adding service:", error);
    }
  };

  const handleLogout = () => {
    localStorage.removeItem("authToken");
    navigate("/login");
  };

  if (loading) return <div>Loading...</div>;

  if (errorMessage) return <div>{errorMessage}</div>;

  return (
    <div className="merchant-dashboard">
      <h1>Merchant Dashboard</h1>

      <div className="profile-section">
        <h2>Your Profile</h2>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            updateProfile();
          }}
        >
          <label>
            Business Name:
            <input
              type="text"
              value={profile.businessName}
              onChange={(e) => setProfile({ ...profile, businessName: e.target.value })}
            />
          </label>
          <label>
            Location:
            <input
              type="text"
              value={profile.location}
              onChange={(e) => setProfile({ ...profile, location: e.target.value })}
            />
          </label>
          <label>
            Description:
            <textarea
              value={profile.description}
              onChange={(e) => setProfile({ ...profile, description: e.target.value })}
            />
          </label>
          <label>
            Hours of Operation:
            <input
              type="text"
              value={profile.hours}
              onChange={(e) => setProfile({ ...profile, hours: e.target.value })}
            />
          </label>
          <button type="submit">Update Profile</button>
        </form>
      </div>

      <div className="services-section">
        <h2>Manage Services</h2>
        <ul>
          {services.map((service) => (
            <li key={service._id}>
              <span>{service.name}</span>
              <button onClick={() => navigate(`/service/${service._id}`)}>View/Edit</button>
            </li>
          ))}
        </ul>

        <h3>Add New Service</h3>
        <form
          onSubmit={(e) => {
            e.preventDefault();
            addService();
          }}
        >
          <label>
            Service Name:
            <input
              type="text"
              value={newService.name}
              onChange={(e) => setNewService({ ...newService, name: e.target.value })}
            />
          </label>
          <label>
            Description:
            <textarea
              value={newService.description}
              onChange={(e) => setNewService({ ...newService, description: e.target.value })}
            />
          </label>
          <label>
            Price:
            <input
              type="number"
              value={newService.price}
              onChange={(e) => setNewService({ ...newService, price: e.target.value })}
            />
          </label>
          <label>
            Slots (comma-separated):
            <input
              type="text"
              value={newService.slots}
              onChange={(e) => setNewService({ ...newService, slots: e.target.value.split(",") })}
            />
          </label>
          <button type="submit">Add Service</button>
        </form>
      </div>

      <button onClick={handleLogout}>Logout</button>
    </div>
  );
};

export default MerchantDashboard;
