import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import { Link } from "react-router-dom";
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';

const BASE_URL = "http://localhost:5000/api/merchants"; // Update this if the base URL changes

const MerchantDashboard = () => {
  const [activeTab, setActiveTab] = useState("viewProfile");
  const [loading, setLoading] = useState(true);
  const [merchant, setMerchant] = useState(null);
  const [profileData, setProfileData] = useState({});
  const [services, setServices] = useState([]);
  const [newService, setNewService] = useState({
    title: "",
    description: "",
    price: "",
    slots: [],
    image: "",
  });
  const [imageSuggestions, setImageSuggestions] = useState([]);
  const navigate = useNavigate();

  const authToken = localStorage.getItem("authToken");

  useEffect(() => {
    if (!authToken) {
      navigate("/auth");
    } else {
      fetchMerchantData();
    }
  }, [authToken, navigate]);

  const fetchMerchantData = async () => {
    try {
      const profileRes = await axios.get(`${BASE_URL}/profile`, {
        headers: { Authorization: authToken },
      });
      setMerchant(profileRes.data);
      setProfileData({
        businessName: profileRes.data.businessName || "",
        location: profileRes.data.location || "",
        description: profileRes.data.description || "",
        hours: profileRes.data.hours || "",
      });

      const servicesRes = await axios.get(`${BASE_URL}/services`, {
        headers: { Authorization: authToken },
      });
      setServices(servicesRes.data);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching merchant data:", error);
      setLoading(false);
    }
  };

  const handleProfileChange = (e) => {
    const { name, value } = e.target;
    setProfileData((prev) => ({ ...prev, [name]: value }));
  };

  const handleBookingStatusChange = async (bookingId) => {
    try {
      const response = await axios.put(
        `${BASE_URL}/bookings/${bookingId}`,
        { status: "confirmed" },
        { headers: { Authorization: authToken } }
      );
      alert("Booking status updated!");
      fetchMerchantData(); // Re-fetch to update booking status
    } catch (error) {
      console.error("Error updating booking status:", error);
    }
  };
  

  const removeSlot = (index) => {
    const updatedSlots = [...newService.slots];
    updatedSlots.splice(index, 1);
    setNewService((prev) => ({ ...prev, slots: updatedSlots }));
  };
  const [template, setTemplate] = useState("default");
  const [slot, setSlot] = useState({ startTime: "", endTime: "" });
  const addSlot = () => {
    if (!slot.startTime || !slot.endTime) {
      alert("Please provide valid start and end times.");
      return;
    }
    setNewService((prev) => ({
      ...prev,
      slots: [...prev.slots, slot],
    }));
    setSlot({ startTime: "", endTime: "" });
  };
  const updateProfile = async () => {
    try {
      const response = await axios.put(
        `${BASE_URL}/profile`,
        { ...profileData },
        { headers: { Authorization: authToken } }
      );
      setMerchant(response.data);
      alert("Profile updated successfully!");
    } catch (error) {
      console.error("Error updating profile:", error);
    }
  };

  const handleServiceChange = (e) => {
    const { name, value } = e.target;
    setNewService((prev) => ({ ...prev, [name]: value }));
  };

 

  const fetchImageSuggestions = async () => {
    // Mock image suggestions using placeholder API (e.g., Unsplash)
    const images = [
      "https://via.placeholder.com/150?text=Service+Image+1",
      "https://via.placeholder.com/150?text=Service+Image+2",
      "https://via.placeholder.com/150?text=Service+Image+3",
    ];
    setImageSuggestions(images);
  };
  const addService = async () => {
    if (!newService.title || !newService.price || !newService.slots.length) {
      alert("Please provide all necessary details.");
      return;
    }
  
    try {
      const response = await axios.post(
        `${BASE_URL}/services`,
        { ...newService, templateId: template },
        { headers: { Authorization: authToken } }
      );
      setServices((prev) => [...prev, response.data]);
      setNewService({
        title: "",
        description: "",
        price: "",
        slots: [],
        image: "",
      });
      setTemplate("default");
      alert("Service added successfully!");
    } catch (error) {
      console.error("Error adding service:", error);
      alert("Failed to add service.");
    }
  };
  

  const deleteService = async (serviceId) => {
    try {
      await axios.delete(`${BASE_URL}/services/${serviceId}`, {
        headers: { Authorization: authToken },
      });
      setServices((prev) => prev.filter((service) => service._id !== serviceId));
      alert("Service deleted successfully!");
    } catch (error) {
      console.error("Error deleting service:", error);
    }
  };

  const logout = () => {
    localStorage.removeItem("authToken");
    navigate("/auth");
  };

  if (loading) {
    return <div className="text-center p-4">Loading...</div>;
  }

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-2xl font-bold">Merchant Dashboard</h1>
        <button
          onClick={logout}
          className="bg-red-500 text-white px-4 py-2 rounded"
        >
          Logout
        </button>
      </div>

      {/* Tabs */}
      <div className="flex mb-6 border-b">
        <button
          onClick={() => setActiveTab("viewProfile")}
          className={`py-2 px-4 ${
            activeTab === "viewProfile" ? "border-b-2 border-blue-500" : ""
          }`}
        >
          View Profile
        </button>
        <button
          onClick={() => setActiveTab("updateProfile")}
          className={`py-2 px-4 ${
            activeTab === "updateProfile" ? "border-b-2 border-blue-500" : ""
          }`}
        >
          Update Profile
        </button>
        <button
          onClick={() => setActiveTab("services")}
          className={`py-2 px-4 ${
            activeTab === "services" ? "border-b-2 border-blue-500" : ""
          }`}
        >
          Services
        </button>
        <button
          onClick={() => setActiveTab("settings")}
          className={`py-2 px-4 ${
            activeTab === "settings" ? "border-b-2 border-blue-500" : ""
          }`}
        >
          Settings
        </button>
      </div>

      {/* View Profile */}
      {activeTab === "viewProfile" && (
        <div className="bg-white p-4 rounded shadow">
          <h2 className="text-xl font-semibold mb-4">Profile Details</h2>
          <p><strong>Business Name:</strong> {profileData.businessName}</p>
          <p><strong>Location:</strong> {profileData.location}</p>
          <p><strong>Description:</strong> {profileData.description}</p>
          <p><strong>Hours:</strong> {profileData.hours}</p>
        </div>
      )}

      {/* Update Profile */}
      {activeTab === "updateProfile" && (
        <div className="bg-white p-4 rounded shadow">
          <h2 className="text-xl font-semibold mb-4">Update Profile</h2>
          <input
            type="text"
            name="businessName"
            value={profileData.businessName}
            onChange={handleProfileChange}
            placeholder="Business Name"
            className="block w-full p-2 border rounded mb-2"
          />
          <input
            type="text"
            name="location"
            value={profileData.location}
            onChange={handleProfileChange}
            placeholder="Location"
            className="block w-full p-2 border rounded mb-2"
          />
          <textarea
            name="description"
            value={profileData.description}
            onChange={handleProfileChange}
            placeholder="Description"
            className="block w-full p-2 border rounded mb-2"
          ></textarea>
          <input
            type="text"
            name="hours"
            value={profileData.hours}
            onChange={handleProfileChange}
            placeholder="Business Hours"
            className="block w-full p-2 border rounded mb-2"
          />
          <button
            onClick={updateProfile}
            className="bg-blue-500 text-white px-4 py-2 rounded"
          >
            Save Changes
          </button>
        </div>
      )}
      <button
  onClick={() => setActiveTab("bookings")}
  className={`py-2 px-4 ${activeTab === "bookings" ? "border-b-2 border-blue-500" : ""}`}
>
  Bookings
</button>

// Add a new "Bookings" section
{activeTab === "bookings" && (
  <div className="bg-white p-4 rounded shadow">
    <h2 className="text-xl font-semibold mb-4">Manage Bookings</h2>
    <ul>
      {services.map((service) => (
        <li key={service._id} className="mb-4">
          <h3 className="font-semibold">{service.title}</h3>
          {/* Add a list of bookings for each service */}
          <ul>
            {service.bookings.map((booking) => (
              <li key={booking._id}>
                <p>{booking.customerName} - {booking.time}</p>
                <button onClick={() => handleBookingStatusChange(booking._id)} className="bg-yellow-500 text-white px-2 py-1 rounded">Change Status</button>
              </li>
            ))}
          </ul>
        </li>
      ))}
    </ul>
  </div>
)}
// Show current slots
{newService.slots.length > 0 && (
  <div>
    <h3 className="text-lg font-semibold mt-6">Available Slots</h3>
    {newService.slots.map((slot, index) => (
      <div key={index} className="mb-2">
        <p>{slot.startTime} - {slot.endTime}</p>
        <button
          onClick={() => removeSlot(index)}
          className="bg-red-500 text-white px-2 py-1 rounded"
        >
          Remove Slot
        </button>
      </div>
    ))}
  </div>
)}


      {/* Services */}
      {activeTab === "services" && (
        <div className="bg-white p-4 rounded shadow">
          <h2 className="text-xl font-semibold mb-4">Manage Services</h2>
          <div>
            <input
              type="text"
              name="title"
              value={newService.title}
              onChange={handleServiceChange}
              placeholder="Service Title"
              className="block w-full p-2 border rounded mb-2"
            />
            <textarea
              name="description"
              value={newService.description}
              onChange={handleServiceChange}
              placeholder="Service Description"
              className="block w-full p-2 border rounded mb-2"
            ></textarea>
            <input
              type="number"
              name="price"
              value={newService.price}
              onChange={handleServiceChange}
              placeholder="Price"
              className="block w-full p-2 border rounded mb-2"
            />
            <div className="flex items-center mb-2">
              <input
                type="time"
                name="startTime"
                value={slot.startTime}
                onChange={(e) =>
                  setSlot((prev) => ({ ...prev, startTime: e.target.value }))
                }
                className="border rounded p-2 mr-2"
              />
              <select
  name="template"
  value={template}
  onChange={(e) => setTemplate(e.target.value)}
  className="w-full p-2 mb-2 border rounded"
>
  <option value="default">Select Template</option>
  <option value="salon">Salon</option>
  <option value="clinic">Clinic</option>
  <option value="shop">Shop</option>
</select>
              <input
                type="time"
                name="endTime"
                value={slot.endTime}
                onChange={(e) =>
                  setSlot((prev) => ({ ...prev, endTime: e.target.value }))
                }
                className="border rounded p-2 mr-2"
              />
              <button
                onClick={addSlot}
                className="bg-blue-500 text-white px-4 py-2 rounded"
              >
                Add Slot
              </button>
            </div>
            <button
              onClick={addService}
              className="bg-green-500 text-white px-4 py-2 rounded"
            >
              Add Service
            </button>
          </div>

          <h3 className="text-lg font-semibold mt-6">Existing Services</h3>
          {services.map((service) => (
            <div
              key={service._id}
              className="bg-gray-100 p-4 rounded shadow mb-2"
            >
              <h4 className="font-semibold">{service.title}</h4>
              <p>{service.description}</p>
              <p>
                <strong>Price:</strong> ${service.price}
              </p>
              <button
                onClick={() => deleteService(service._id)}
                className="bg-red-500 text-white px-2 py-1 rounded mt-2"
              >
                Delete
              </button>
              <div className="p-6">
      <h1 className="text-2xl font-bold">My Services</h1>
      <ul>
        {services.map((service) => (
          <li key={service.id} className="mb-4">
            <h2 className="text-xl font-semibold">{service.title}</h2>
            <p>{service.description}</p>
            <Link
              to={`/services/${service._id}`}
              className="text-blue-500 underline"
            >
              View Details
            </Link>
          </li>
        ))}
      </ul>
    </div>
            </div>

          ))}
        </div>
      )}

      {/* Settings */}
      {activeTab === "settings" && (
        <div className="bg-white p-4 rounded shadow">
          <h2 className="text-xl font-semibold">Settings</h2>
          <p>Additional customization settings go here...</p>
        </div>
      )}
    </div>
  );
};

export default MerchantDashboard;
