import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MerchantDashboard = () => {
  const [merchant, setMerchant] = useState(null);
  const [services, setServices] = useState([]);
  const merchantId = localStorage.getItem('merchantId'); // Ensure this is stored correctly

  useEffect(() => {
    const fetchMerchantData = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/merchant/${merchantId}`, {
          headers: { Authorization: localStorage.getItem('authToken') },
        });
        setMerchant(response.data);
        setServices(response.data.services);
      } catch (err) {
        console.error('Error fetching merchant data:', err);
      }
    };

    fetchMerchantData();
  }, [merchantId]);

  const handleAddService = async (newService) => {
    try {
      const response = await axios.post(
        `http://localhost:5000/api/merchant/${merchantId}/services`,
        newService,
        { headers: { Authorization: localStorage.getItem('authToken') } }
      );
      setServices(response.data.services);
    } catch (err) {
      console.error('Error adding service:', err);
    }
  };

  return (
    <div className="dashboard">
      <h1>Welcome, {merchant?.businessName}</h1>
      <div>
        <h2>Your Services</h2>
        {services.map((service) => (
          <div key={service._id}>{service.name}</div>
        ))}
        {/* Add functionality for adding/editing services */}
      </div>
    </div>
  );
};

export default MerchantDashboard;
