import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const MerchantDashboard = () => {
  const [profile, setProfile] = useState(null);
  const [loading, setLoading] = useState(true);
  const [errorMessage, setErrorMessage] = useState("");

  const navigate = useNavigate();

  useEffect(() => {
    const token = localStorage.getItem("authToken");
    if (!token) {
      navigate("/login");
      return;
    }

    const fetchProfile = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/merchants", {
          headers: { Authorization: `Bearer ${token}` },
        });
        setProfile(response.data);
      } catch (error) {
        setErrorMessage("Error fetching profile");
      } finally {
        setLoading(false);
      }
    };

    fetchProfile();
  }, [navigate]);

  const updateProfile = async (updates) => {
    const token = localStorage.getItem("authToken");
    try {
      const response = await axios.put("http://localhost:5000/api/merchants/profile", updates, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setProfile(response.data);
      alert("Profile updated successfully");
    } catch (error) {
      console.error("Error updating profile:", error);
    }
  };

  if (loading) return <div>Loading...</div>;
  if (errorMessage) return <div>{errorMessage}</div>;

  return (
    <div>
      <h1>Merchant Dashboard</h1>
      <form
        onSubmit={(e) => {
          e.preventDefault();
          updateProfile(profile);
        }}
      >
        <label>
          Business Name:
          <input
            type="text"
            value={profile.businessName || ""}
            onChange={(e) => setProfile({ ...profile, businessName: e.target.value })}
          />
        </label>
        <label>
          Location:
          <input
            type="text"
            value={profile.location || ""}
            onChange={(e) => setProfile({ ...profile, location: e.target.value })}
          />
        </label>
        <label>
          Description:
          <textarea
            value={profile.description || ""}
            onChange={(e) => setProfile({ ...profile, description: e.target.value })}
          />
        </label>
        <label>
          Hours of Operation:
          <input
            type="text"
            value={profile.hours || ""}
            onChange={(e) => setProfile({ ...profile, hours: e.target.value })}
          />
        </label>
        <button type="submit">Update Profile</button>
      </form>
    </div>
  );
};

export default MerchantDashboard;
