  const [merchant, setMerchant] = useState(null);
  const [profileData, setProfileData] = useState({});
  const [services, setServices] = useState([]);
  const [newService, setNewService] = useState({
    title: "",
    description: "",
    price: "",
    slots: [],
    image: "",
  });
  const [slot, setSlot] = useState({ startTime: "", endTime: "" });
  const [template, setTemplate] = useState("default");
  const [imageSuggestions, setImageSuggestions] = useState([]);
  const navigate = useNavigate();

  const authToken = localStorage.getItem("authToken");

  useEffect(() => {
    if (!authToken) {
      navigate("/auth");
    } else {
      fetchMerchantData();
    }
  }, [authToken, navigate]);

  const fetchMerchantData = async () => {
    try {
      const profileRes = await axios.get(`${BASE_URL}/profile`, {
        headers: { Authorization: authToken },
      });
      setMerchant(profileRes.data);
      setProfileData({
        businessName: profileRes.data.businessName || "",
        location: profileRes.data.location || "",
        description: profileRes.data.description || "",
        hours: profileRes.data.hours || "",
      });

      const servicesRes = await axios.get(`${BASE_URL}/services`, {
        headers: { Authorization: authToken },
      });
      setServices(servicesRes.data);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching merchant data:", error);
      setLoading(false);
    }
  };

  const handleProfileChange = (e) => {
    const { name, value } = e.target;
    setProfileData((prev) => ({ ...prev, [name]: value }));
  };

  const updateProfile = async () => {
    try {
      const response = await axios.put(
        `${BASE_URL}/profile`,
        { ...profileData },
        { headers: { Authorization: authToken } }
      );
      setMerchant(response.data);
      alert("Profile updated successfully!");
    } catch (error) {
      console.error("Error updating profile:", error);
    }
  };

  const handleServiceChange = (e) => {
    const { name, value } = e.target;
    setNewService((prev) => ({ ...prev, [name]: value }));
  };

  const addSlot = () => {
    if (!slot.startTime || !slot.endTime) {
      alert("Please provide valid start and end times.");
      return;
    }
    setNewService((prev) => ({
      ...prev,
      slots: [...prev.slots, slot],
    }));
    setSlot({ startTime: "", endTime: "" });
  };

  const addService = async () => {
    if (!newService.title || !newService.price || !newService.slots.length) {
      alert("Please provide all necessary details.");
      return;
    }

    try {
      const response = await axios.post(
        `${BASE_URL}/services`,
        { ...newService, templateId: template },
        { headers: { Authorization: authToken } }
      );
      setServices((prev) => [...prev, response.data]);
      setNewService({
        title: "",
        description: "",
        price: "",
        slots: [],
        image: "",
      });
      setTemplate("default");
      alert("Service added successfully!");
    } catch (error) {
      console.error("Error adding service:", error);
      alert("Failed to add service.");
    }
  };

  const fetchImageSuggestions = async () => {
    const images = [
      "https://via.placeholder.com/150?text=Service+Image+1",
      "https://via.placeholder.com/150?text=Service+Image+2",
      "https://via.placeholder.com/150?text=Service+Image+3",
    ];
    setImageSuggestions(images);
  };

  const deleteService = async (serviceId) => {
    try {
      await axios.delete(`${BASE_URL}/services/${serviceId}`, {
        headers: { Authorization: authToken },
      });
      setServices((prev) => prev.filter((service) => service._id !== serviceId));
      alert("Service deleted successfully!");
    } catch (error) {
      console.error("Error deleting service:", error);
    }
  };

  const logout = () => {
    localStorage.removeItem("authToken");
    navigate("/auth");
  };

  if (loading) {
    return <div className="text-center p-4">Loading...</div>;
  }

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-2xl font-bold">Merchant Dashboard</h1>
        <button
          onClick={logout}
          className="bg-red-500 text-white px-4 py-2 rounded"
        >
          Logout
        </button>
      </div>

      {/* Tabs */}
      <div className="flex mb-6 border-b">
        <button
          onClick={() => setActiveTab("viewProfile")}
          className={`py-2 px-4 ${
            activeTab === "viewProfile" ? "border-b-2 border-blue-500" : ""
          }`}
        >
          View Profile
        </button>
        <button
          onClick={() => setActiveTab("updateProfile")}
          className={`py-2 px-4 ${
            activeTab === "updateProfile" ? "border-b-2 border-blue-500" : ""
          }`}
        >
          Update Profile
        </button>
        <button
          onClick={() => setActiveTab("services")}
          className={`py-2 px-4 ${
            activeTab === "services" ? "border-b-2 border-blue-500" : ""
          }`}
        >
          Services
        </button>
        <button
          onClick={() => setActiveTab("settings")}
          className={`py-2 px-4 ${
            activeTab === "settings" ? "border-b-2 border-blue-500" : ""
          }`}
        >
          Settings
        </button>
      </div>

      {/* Tab Content */}
      <div>
        {activeTab === "viewProfile" && (
          <div>
            <h2 className="text-xl font-bold mb-4">View Profile</h2>
            <div className="mb-4">
              <div>
                <strong>Business Name:</strong> {merchant.businessName}
              </div>
              <div>
                <strong>Location:</strong> {merchant.location}
              </div>
              <div>
                <strong>Description:</strong> {merchant.description}
              </div>
              <div>
                <strong>Hours:</strong> {merchant.hours}
              </div>
            </div>
          </div>
        )}

        {activeTab === "updateProfile" && (
          <div>
            <h2 className="text-xl font-bold mb-4">Update Profile</h2>
            <input
              type="text"
              name="businessName"
              value={profileData.businessName}
              onChange={handleProfileChange}
              className="w-full p-2 mb-2 border rounded"
              placeholder="Business Name"
            />
            <input
              type="text"
              name="location"
              value={profileData.location}
              onChange={handleProfileChange}
              className="w-full p-2 mb-2 border rounded"
              placeholder="Location"
            />
            <textarea
              name="description"
              value={profileData.description}
              onChange={handleProfileChange}
              className="w-full p-2 mb-2 border rounded"
              placeholder="Business Description"
            />
            <input
              type="text"
              name="hours"
              value={profileData.hours}
              onChange={handleProfileChange}
              className="w-full p-2 mb-2 border rounded"
              placeholder="Business Hours"
            />
            <button
              onClick={updateProfile}
              className="bg-blue-500 text-white px-4 py-2 rounded mt-2"
            >
              Update Profile
            </button>
          </div>
        )}

        {activeTab === "services" && (
          <div>
            <h2 className="text-xl font-bold mb-4">Manage Services</h2>

            <div className="mb-4">
              <input
                type="text"
                name="title"
                value={newService.title}
                onChange={handleServiceChange}
                className="w-full p-2 mb-2 border rounded"
                placeholder="Service Title"
              />
              <textarea
                name="description"
                value={newService.description}
                onChange={handleServiceChange}
                className="w-full p-2 mb-2 border rounded"
                placeholder="Service Description"
              />
              <input
                type="number"
                name="price"
                value={newService.price}
                onChange={handleServiceChange}
                className="w-full p-2 mb-2 border rounded"
                placeholder="Price (INR)"
              />
              <button
                onClick={addSlot}
                className="bg-green-500 text-white px-4 py-2 rounded"
              >
                Add Slot
              </button>

              {newService.slots.map((slot, index) => (
                <div key={index} className="mb-2">
                  <span>
                    {slot.startTime} - {slot.endTime}
                  </span>
                  <button
                    onClick={() =>
                      setNewService({
                        ...newService,
                        slots: newService.slots.filter(
                          (_, idx) => idx !== index
                        ),
                      })
                    }
                    className="text-red-500 ml-2"
                  >
                    Remove
                  </button>
                </div>
              ))}

              <button
                onClick={addService}
                className="bg-blue-500 text-white px-4 py-2 rounded mt-4"
              >
                Add Service
              </button>
            </div>

            <h3 className="text-lg font-semibold">Existing Services</h3>
            {services.length === 0 ? (
              <p>No services found</p>
            ) : (
              <ul>
                {services.map((service) => (
                  <li key={service._id} className="mb-2">
                    <div>
                      <strong>{service.title}</strong>
                      <button
                        onClick={() => deleteService(service._id)}
                        className="text-red-500 ml-2"
                      >
                        Delete
                      </button>
                    </div>
                    <p>{service.description}</p>
                    <p>Price: â‚¹{service.price}</p>
                  </li>
                ))}
              </ul>
            )}
          </div>
        )}

        {activeTab === "settings" && (
          <div>
            <h2 className="text-xl font-bold mb-4">Settings</h2>
            <p>Settings section coming soon!</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default MerchantDashboard;
