import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const ServiceDetails = () => {
  const { id: serviceId } = useParams(); // Service ID from URL
  const [service, setService] = useState(null); // Service details
  const [isMerchant, setIsMerchant] = useState(false); // Role check
  const [editMode, setEditMode] = useState(false); // Toggle edit mode
  const [newSlot, setNewSlot] = useState({ startTime: "", endTime: "", recurrence: "none" }); // For adding slots
  const [updatedService, setUpdatedService] = useState({}); // Editable service details
  const authToken = localStorage.getItem("authToken"); // Auth token from local storage

  useEffect(() => {
    fetchServiceDetails();
    determineUserRole();
  }, [serviceId]);

  // Fetch service details
  const fetchServiceDetails = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/services/${serviceId}`, {
        headers: { Authorization: `Bearer ${authToken}` },
      });
      setService(response.data);
      setUpdatedService(response.data); // Pre-fill for editing
    } catch (error) {
      console.error("Error fetching service details:", error);
    }
  };

  // Determine if the logged-in user is a merchant
  const determineUserRole = () => {
    const role = localStorage.getItem("userRole"); // Set during login
    setIsMerchant(role === "merchant");
  };

  // Handle field updates for editing service
  const handleFieldChange = (e) => {
    const { name, value } = e.target;
    setUpdatedService((prev) => ({ ...prev, [name]: value }));
  };

  // Save changes made by the merchant
  const saveChanges = async () => {
    try {
      const response = await axios.put(
        `http://localhost:5000/api/services/${serviceId}`,
        updatedService,
        { headers: { Authorization: `Bearer ${authToken}` } }
      );
      setService(response.data); // Update service details
      setEditMode(false); // Exit edit mode
      alert("Service updated successfully!");
    } catch (error) {
      console.error("Error updating service details:", error);
    }
  };

  // Add a new slot
  const addSlot = async () => {
    try {
      const response = await axios.post(
        `http://localhost:5000/api/services/${serviceId}/slots`,
        newSlot,
        { headers: { Authorization: `Bearer ${authToken}` } }
      );
      setService(response.data); // Update service with the new slot
      setNewSlot({ startTime: "", endTime: "", recurrence: "none" }); // Reset slot form
    } catch (error) {
      console.error("Error adding slot:", error);
    }
  };

  // Book a slot (customer action)
  const bookSlot = async (slotId) => {
    try {
      await axios.post(
        `http://localhost:5000/api/services/${serviceId}/book`,
        { slotId },
        { headers: { Authorization: `Bearer ${authToken}` } }
      );
      alert("Slot booked successfully!");
      fetchServiceDetails(); // Refresh details
    } catch (error) {
      console.error("Error booking slot:", error);
    }
  };

  if (!service) return <p>Loading service details...</p>; // Loading state

  return (
    <div className="min-h-screen p-6 bg-gray-100">
      {/* Service Banner */}
      <div
        className="relative bg-cover bg-center h-64 rounded shadow"
        style={{
          backgroundImage: `url(${service.images?.[0] || "https://via.placeholder.com/1200x400"})`,
        }}
      >
        {isMerchant && editMode && (
          <input
            type="text"
            name="images[0]"
            value={updatedService.images?.[0] || ""}
            onChange={(e) => setUpdatedService({ ...updatedService, images: [e.target.value] })}
            className="absolute bottom-4 left-4 p-2 bg-white rounded shadow"
            placeholder="Banner Image URL"
          />
        )}
        <h1 className="absolute bottom-4 left-4 text-white text-3xl font-bold bg-black bg-opacity-50 px-4 py-2 rounded">
          {editMode ? (
            <input
              type="text"
              name="title"
              value={updatedService.title}
              onChange={handleFieldChange}
              className="p-2 bg-white rounded"
            />
          ) : (
            service.title
          )}
        </h1>
      </div>

      <div className="bg-white p-6 rounded shadow mt-6">
        {/* Service Description */}
        <p className="text-lg">
          {editMode ? (
            <textarea
              name="description"
              value={updatedService.description}
              onChange={handleFieldChange}
              className="p-2 w-full border rounded"
            ></textarea>
          ) : (
            service.description
          )}
        </p>

        {/* Service Price */}
        <p className="text-xl font-bold mt-4">
          Price:{" "}
          {editMode ? (
            <input
              type="number"
              name="price"
              value={updatedService.price}
              onChange={handleFieldChange}
              className="p-2 border rounded"
            />
          ) : (
            `$${service.price}`
          )}
        </p>

        {/* Merchant Contact Info */}
        <p className="mt-4">
          <strong>Contact:</strong> {service.merchantId?.email} | {service.merchantId?.phone}
        </p>

        {/* Save Button for Merchant */}
        {isMerchant && editMode && (
          <button
            onClick={saveChanges}
            className="bg-green-500 text-white px-4 py-2 rounded mt-4"
          >
            Save Changes
          </button>
        )}

        {isMerchant && (
          <button
            onClick={() => setEditMode(!editMode)}
            className="bg-gray-500 text-white px-4 py-2 rounded mt-4 ml-4"
          >
            {editMode ? "Cancel Edit" : "Edit Service"}
          </button>
        )}
      </div>

      {/* Slots Section */}
      <div className="bg-white p-6 rounded shadow mt-6">
        <h2 className="text-2xl font-semibold">Available Slots</h2>
        <ul className="mt-4">
          {service.slots.map((slot, index) => (
            <li key={index} className="flex justify-between items-center mb-2">
              {slot.startTime} - {slot.endTime} ({slot.recurrence})
              {!slot.isBooked && !isMerchant && (
                <button
                  onClick={() => bookSlot(slot._id)}
                  className="bg-blue-500 text-white px-4 py-2 rounded"
                >
                  Book
                </button>
              )}
            </li>
          ))}
        </ul>

        {/* Add Slots for Merchant */}
        {isMerchant && (
          <div className="mt-6">
            <h3 className="text-lg font-semibold">Add a New Slot</h3>
            <div className="flex items-center mt-4">
              <input
                type="time"
                value={newSlot.startTime}
                onChange={(e) => setNewSlot({ ...newSlot, startTime: e.target.value })}
                className="border p-2 mr-2 rounded"
              />
              <input
                type="time"
                value={newSlot.endTime}
                onChange={(e) => setNewSlot({ ...newSlot, endTime: e.target.value })}
                className="border p-2 mr-2 rounded"
              />
              <select
                value={newSlot.recurrence}
                onChange={(e) => setNewSlot({ ...newSlot, recurrence: e.target.value })}
                className="border p-2 mr-2 rounded"
              >
                <option value="none">None</option>
                <option value="daily">Daily</option>
                <option value="weekly">Weekly</option>
              </select>
              <button
                onClick={addSlot}
                className="bg-green-500 text-white px-4 py-2 rounded"
              >
                Add Slot
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ServiceDetails;
