import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";

const BASE_URL = "http://localhost:5000/api/merchants";

const MerchantDashboard = () => {
  const [activeTab, setActiveTab] = useState("viewProfile");
  const [loading, setLoading] = useState(true);
  const [merchant, setMerchant] = useState(null);
  const [profileData, setProfileData] = useState({});
  const [services, setServices] = useState([]);
  const [newService, setNewService] = useState({
    title: "",
    description: "",
    price: "",
    templateId:"defualt",
  });
  const [slot, setSlot] = useState({ startTime: "", endTime: "", recurrence: "none" });
  const [slots, setSlots] = useState([]);

  const navigate = useNavigate();
  const authToken = localStorage.getItem("authToken");
  const [showAddServiceForm, setShowAddServiceForm] = useState(false);

  useEffect(() => {
    if (!authToken) {
      navigate("/auth");
    } else {
      fetchMerchantData();
    }
  }, [authToken, navigate]);

  const fetchMerchantData = async () => {
    try {
      const profileRes = await axios.get(`${BASE_URL}/profile`, {
        headers: { Authorization: authToken },
      });
      setMerchant(profileRes.data);
      setProfileData({
        businessName: profileRes.data.businessName || "",
        location: profileRes.data.location || "",
        description: profileRes.data.description || "",
        hours: profileRes.data.hours || "",
      });

      const servicesRes = await axios.get(`${BASE_URL}/services`, {
        headers: { Authorization: authToken },
      });
      setServices(servicesRes.data);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching merchant data:", error);
      setLoading(false);
    }
  };

  const handleProfileChange = (e) => {
    const { name, value } = e.target;
    setProfileData((prev) => ({ ...prev, [name]: value }));
  };

  const updateProfile = async () => {
    try {
      const response = await axios.put(
        `${BASE_URL}/profile`,
        { ...profileData },
        { headers: { Authorization: authToken } }
      );
      setMerchant(response.data);
      alert("Profile updated successfully!");
    } catch (error) {
      console.error("Error updating profile:", error);
    }
  };

  const handleServiceChange = (e) => {
    const { name, value } = e.target;
    setNewService((prev) => ({ ...prev, [name]: value }));
  };

  const handleSlotChange = (e) => {
    const { name, value } = e.target;
    setSlot((prev) => ({ ...prev, [name]: value }));
  };

  const addSlot = () => {
    if (!slot.startTime || !slot.endTime) {
      alert("Please provide valid slot timings.");
      return;
    }
    setSlots((prev) => [...prev, slot]);
    setSlot({ startTime: "", endTime: "", recurrence: "none" });
  };

  const addService = async () => {
    if (!newService.title || !newService.price || slots.length === 0) {
      alert("Please complete all fields and add at least one slot.");
      return;
    }
    try {
      const response = await axios.post(
        `${BASE_URL}/services`,
        { ...newService, slots },
        { headers: { Authorization: authToken } }
      );
      setServices((prev) => [...prev, response.data]);
      setNewService({ title: "", description: "", price: "" });
      setSlots([]);
      alert("Service added successfully!");
    } catch (error) {
      console.error("Error adding service:", error);
      alert("Failed to add service.");
    }
  };

  const deleteService = async (serviceId) => {
    try {
      await axios.delete(`${BASE_URL}/services/${serviceId}`, {
        headers: { Authorization: authToken },
      });
      setServices((prev) => prev.filter((service) => service._id !== serviceId));
      alert("Service deleted successfully!");
    } catch (error) {
      console.error("Error deleting service:", error);
    }
  };

  const logout = () => {
    localStorage.removeItem("authToken");
    navigate("/auth");
  };

  if (loading) {
    return <div className="text-center p-4">Loading...</div>;
  }

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-2xl font-bold">Merchant Dashboard</h1>
        <button
          onClick={logout}
          className="bg-red-500 text-white px-4 py-2 rounded"
        >
          Logout
        </button>
      </div>

      {/* Tabs */}
      <div className="flex mb-6 border-b">
        <button
          onClick={() => setActiveTab("viewProfile")}
          className={`py-2 px-4 ${
            activeTab === "viewProfile" ? "border-b-2 border-blue-500" : ""
          }`}
        >
          View Profile
        </button>
        <button
          onClick={() => setActiveTab("updateProfile")}
          className={`py-2 px-4 ${
            activeTab === "updateProfile" ? "border-b-2 border-blue-500" : ""
          }`}
        >
          Update Profile
        </button>
        <button
          onClick={() => setActiveTab("services")}
          className={`py-2 px-4 ${
            activeTab === "services" ? "border-b-2 border-blue-500" : ""
          }`}
        >
          Services
        </button>
        <button
          onClick={() => setActiveTab("settings")}
          className={`py-2 px-4 ${
            activeTab === "settings" ? "border-b-2 border-blue-500" : ""
          }`}
        >
          Settings
        </button>
      </div>

      {/* View Profile */}
      {activeTab === "viewProfile" && (
        <div className="bg-white p-4 rounded shadow">
          <h2 className="text-xl font-semibold mb-4">Profile Details</h2>
          <p><strong>Business Name:</strong> {profileData.businessName}</p>
          <p><strong>Location:</strong> {profileData.location}</p>
          <p><strong>Description:</strong> {profileData.description}</p>
          <p><strong>Hours:</strong> {profileData.hours}</p>
        </div>
      )}

      {/* Update Profile */}
      {activeTab === "updateProfile" && (
        <div className="bg-white p-4 rounded shadow">
          <h2 className="text-xl font-semibold mb-4">Update Profile</h2>
          <input
            type="text"
            name="businessName"
            value={profileData.businessName}
            onChange={handleProfileChange}
            placeholder="Business Name"
            className="block w-full p-2 border rounded mb-2"
          />
          <input
            type="text"
            name="location"
            value={profileData.location}
            onChange={handleProfileChange}
            placeholder="Location"
            className="block w-full p-2 border rounded mb-2"
          />
          <textarea
            name="description"
            value={profileData.description}
            onChange={handleProfileChange}
            placeholder="Description"
            className="block w-full p-2 border rounded mb-2"
          ></textarea>
          <input
            type="text"
            name="hours"
            value={profileData.hours}
            onChange={handleProfileChange}
            placeholder="Business Hours"
            className="block w-full p-2 border rounded mb-2"
          />
          <button
            onClick={updateProfile}
            className="bg-blue-500 text-white px-4 py-2 rounded"
          >
            Save Changes
          </button>
        </div>
      )}

      {/* Services */}
{activeTab === "services" && (
  <div className="bg-white p-4 rounded shadow">
    <h2 className="text-xl font-semibold mb-4">Manage Services</h2>

    {/* Add New Service Button */}
    <button
      onClick={() => setShowAddServiceForm(!showAddServiceForm)}
      className="bg-blue-500 text-white px-4 py-2 rounded mb-4"
    >
      {showAddServiceForm ? "Hide Add Service Form" : "Add New Service"}
    </button>

    {/* Add New Service Form */}
    {showAddServiceForm && (
      <div className="mb-6">
        <h3 className="text-lg font-semibold mb-2">Add New Service</h3>
        <input
          type="text"
          name="title"
          value={newService.title}
          onChange={handleServiceChange}
          placeholder="Service Title"
          className="block w-full p-2 border rounded mb-2"
        />
        <textarea
          name="description"
          value={newService.description}
          onChange={handleServiceChange}
          placeholder="Service Description"
          className="block w-full p-2 border rounded mb-2"
        ></textarea>
        <input
          type="number"
          name="price"
          value={newService.price}
          onChange={handleServiceChange}
          placeholder="Price"
          className="block w-full p-2 border rounded mb-2"
        />
        <div className="flex items-center mb-2">
          <input
            type="time"
            name="startTime"
            value={slot.startTime}
            onChange={handleSlotChange}
            className="border rounded p-2 mr-2"
          />
          <input
            type="time"
            name="endTime"
            value={slot.endTime}
            onChange={handleSlotChange}
            className="border rounded p-2 mr-2"
          />
          <select
            name="recurrence"
            value={slot.recurrence}
            onChange={handleSlotChange}
            className="border rounded p-2 mr-2"
          >
            <option value="none">None</option>
            <option value="daily">Daily</option>
            <option value="weekly">Weekly</option>
          </select>
          <button
            onClick={addSlot}
            className="bg-blue-500 text-white px-4 py-2 rounded"
          >
            Add Slot
          </button>
        </div>
        <select
          name="templateId"
          value={newService.templateId || ""}
          onChange={handleServiceChange}
          className="block w-full p-2 border rounded mb-2"
        >
          <option value="">Select Template</option>
          <option value="default">Default Template</option>
          <option value="premium">Premium Template</option>
        </select>
        <div className="mt-2">
          <h4 className="font-semibold">Slots Added:</h4>
          {slots.map((s, idx) => (
            <p key={idx}>
              {s.startTime} - {s.endTime} ({s.recurrence})
            </p>
          ))}
        </div>
        <button
          onClick={addService}
          className="bg-green-500 text-white px-4 py-2 rounded mt-4"
        >
          Add Service
        </button>
      </div>
    )}

    {/* Existing Services */}
    <h3 className="text-lg font-semibold mb-2">Your Services</h3>
    {services.length > 0 ? (
      services.map((service) => (
        <div key={service._id} className="bg-gray-100 p-4 rounded mb-4 shadow">
          <h4 className="font-semibold">{service.title}</h4>
          <p>{service.description}</p>
          <p>
            <strong>Price:</strong> ${service.price}
          </p>
          <Link
            to={`/services/${service._id}`}
            className="text-blue-500 underline"
          >
            Manage Slots
          </Link>
          <button
            onClick={() => deleteService(service._id)}
            className="bg-red-500 text-white px-4 py-2 rounded mt-2"
          >
            Delete Service
          </button>
        </div>
      ))
    ) : (
      <p>No services added yet.</p>
    )}
  

  </div>
)}
    </div>
  );
};

export default MerchantDashboard;
