import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

const MerchantDashboard = () => {
  const { merchantId } = useParams();
  const navigate = useNavigate();
  const [merchantData, setMerchantData] = useState(null);

  // Fetch merchant data on mount
  useEffect(() => {
    const fetchMerchantData = async () => {
      try {
        const token = localStorage.getItem("authToken");
        const response = await axios.get(`http://localhost:5000/api/merchant/${merchantId}`, {
          headers: { Authorization: `Bearer ${token}` },
        });
        setMerchantData(response.data);
      } catch (error) {
        console.error("Error fetching merchant data:", error);
        if (error.response.status === 401) navigate("/auth");
      }
    };

    fetchMerchantData();
  }, [merchantId, navigate]);

  const handleLogout = () => {
    localStorage.clear();
    navigate("/auth");
  };

  return (
    <div className="p-8 bg-gray-100 h-screen">
      <header className="flex justify-between items-center bg-white p-4 shadow-md">
        <h1 className="text-xl font-bold">Merchant Dashboard</h1>
        <button
          onClick={handleLogout}
          className="px-4 py-2 bg-red-500 text-white rounded-md"
        >
          Logout
        </button>
      </header>
      {merchantData ? (
        <div className="mt-8">
          <h2 className="text-2xl font-bold">Welcome, {merchantData.name}</h2>
          <div className="mt-4 bg-white p-4 rounded-lg shadow-md">
            <h3 className="text-lg font-bold">Profile Details</h3>
            <p><strong>Business Name:</strong> {merchantData.businessName || "Not Set"}</p>
            <p><strong>Email:</strong> {merchantData.email}</p>
            <p><strong>Phone:</strong> {merchantData.phone}</p>
            <p><strong>Address:</strong> {merchantData.address || "Not Set"}</p>
          </div>
        </div>
      ) : (
        <p>Loading profile...</p>
      )}
    </div>
  );
};

export default MerchantDashboard;
