import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";

const MerchantDashboard = () => {
  const [merchant, setMerchant] = useState({});
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchMerchantProfile = async () => {
      try {
        const authToken = localStorage.getItem("authToken");
        const response = await axios.get("http://localhost:5000/api/merchants/profile", {
          headers: {
            Authorization: `Bearer ${authToken}`,
          },
        });
        setMerchant(response.data);
        setLoading(false);
      } catch (err) {
        setError("Failed to load merchant profile.");
        setLoading(false);
      }
    };
    fetchMerchantProfile();
  }, []);

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <nav className="bg-blue-600 text-white p-4">
        <div className="flex justify-between items-center container mx-auto">
          <h1 className="text-2xl font-bold">Merchant Dashboard</h1>
          <button
            onClick={handleLogout}
            className="bg-red-500 hover:bg-red-600 px-4 py-2 rounded-md"
          >
            Logout
          </button>
        </div>
      </nav>

      {loading ? (
        <div className="flex items-center justify-center h-[80vh]">
          <p className="text-lg font-semibold text-gray-600">Loading...</p>
        </div>
      ) : error ? (
        <div className="flex items-center justify-center h-[80vh]">
          <p className="text-lg font-semibold text-red-500">{error}</p>
        </div>
      ) : (
        <div className="container mx-auto py-8">
          <div className="bg-white shadow-md rounded-lg p-6">
            <h2 className="text-2xl font-bold text-gray-800 mb-4">Profile</h2>
            <table className="table-auto w-full text-left text-gray-700">
              <tbody>
                <tr>
                  <th className="p-2 font-semibold">Name</th>
                  <td className="p-2">{merchant.name}</td>
                </tr>
                <tr>
                  <th className="p-2 font-semibold">Email</th>
                  <td className="p-2">{merchant.email}</td>
                </tr>
                <tr>
                  <th className="p-2 font-semibold">Phone</th>
                  <td className="p-2">{merchant.phone}</td>
                </tr>
                <tr>
                  <th className="p-2 font-semibold">Business Name</th>
                  <td className="p-2">{merchant.businessName || "Not provided"}</td>
                </tr>
                <tr>
                  <th className="p-2 font-semibold">Location</th>
                  <td className="p-2">{merchant.location || "Not provided"}</td>
                </tr>
                <tr>
                  <th className="p-2 font-semibold">Description</th>
                  <td className="p-2">{merchant.description || "Not provided"}</td>
                </tr>
                <tr>
                  <th className="p-2 font-semibold">Operating Hours</th>
                  <td className="p-2">{merchant.hours || "Not provided"}</td>
                </tr>
              </tbody>
            </table>
          </div>

          <div className="mt-8 bg-white shadow-md rounded-lg p-6">
            <h2 className="text-2xl font-bold text-gray-800 mb-4">Services</h2>
            {merchant.services.length > 0 ? (
              <ul className="list-disc list-inside">
                {merchant.services.map((service, index) => (
                  <li key={index} className="mb-2">
                    <span className="font-semibold">{service.title}</span>:{" "}
                    {service.description} (${service.price})
                  </li>
                ))}
              </ul>
            ) : (
              <p className="text-gray-600">No services added yet.</p>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default MerchantDashboard;
