import React, { useState, useEffect } from "react";
import axios from "axios";

const MerchantDashboard = () => {
  const [profile, setProfile] = useState({ businessName: "", address: "" });
  const [services, setServices] = useState([]);
  const [newService, setNewService] = useState({ serviceName: "", price: "", slots: [] });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [successMessage, setSuccessMessage] = useState("");

  // Fetch profile on component mount
  useEffect(() => {
    fetchMerchantProfile();
  }, []);

  // Fetch merchant profile
  const fetchMerchantProfile = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem("authToken");
      const response = await axios.get("http://localhost:5000/api/merchants/profile", {
        headers: { Authorization: token },
      });

      setProfile({
        businessName: response.data.profile.businessName || "",
        address: response.data.profile.address || "",
      });
      setServices(response.data.profile.services || []);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching profile:", error);
      setError("Failed to load profile. Please try again.");
      setLoading(false);
    }
  };

  // Update merchant profile
  const updateMerchantProfile = async () => {
    try {
      const token = localStorage.getItem("authToken");
      const updatedData = {
        ...profile,
        services,
      };

      await axios.put("http://localhost:5000/api/merchants/profile", updatedData, {
        headers: { Authorization: token },
      });
      setSuccessMessage("Profile updated successfully!");
      setError("");
    } catch (error) {
      console.error("Error updating profile:", error);
      setError("Failed to update profile. Please try again.");
      setSuccessMessage("");
    }
  };

  // Add new service
  const handleServiceAdd = () => {
    if (!newService.serviceName || !newService.price) {
      setError("Service name and price are required.");
      return;
    }

    setServices([...services, { ...newService, slots: [] }]);
    setNewService({ serviceName: "", price: "", slots: [] });
    setError("");
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="container mx-auto p-6">
      <h1 className="text-2xl font-bold mb-4">Merchant Dashboard</h1>

      {/* Error and Success Messages */}
      {error && <p className="text-red-500">{error}</p>}
      {successMessage && <p className="text-green-500">{successMessage}</p>}

      {/* Profile Section */}
      <div className="mb-6">
        <h2 className="text-xl font-semibold">Profile</h2>
        <input
          type="text"
          placeholder="Business Name"
          value={profile.businessName}
          onChange={(e) => setProfile({ ...profile, businessName: e.target.value })}
          className="block border rounded-md p-2 w-full mt-2"
        />
        <input
          type="text"
          placeholder="Address"
          value={profile.address}
          onChange={(e) => setProfile({ ...profile, address: e.target.value })}
          className="block border rounded-md p-2 w-full mt-2"
        />
      </div>

      {/* Services Section */}
      <div className="mb-6">
        <h2 className="text-xl font-semibold">Services</h2>
        {services.length === 0 ? (
          <p>No services added yet.</p>
        ) : (
          services.map((service, index) => (
            <div key={index} className="p-2 border rounded-md mb-2">
              <h3 className="font-medium">{service.serviceName}</h3>
              <p>Price: {service.price}</p>
              <p>Slots: {service.slots.length > 0 ? service.slots.join(", ") : "No slots added"}</p>
            </div>
          ))
        )}
        <div className="p-4 border rounded-md mt-4">
          <h3 className="font-semibold mb-2">Add New Service</h3>
          <input
            type="text"
            placeholder="Service Name"
            value={newService.serviceName}
            onChange={(e) => setNewService({ ...newService, serviceName: e.target.value })}
            className="block border rounded-md p-2 w-full mt-2"
          />
          <input
            type="number"
            placeholder="Price"
            value={newService.price}
            onChange={(e) => setNewService({ ...newService, price: e.target.value })}
            className="block border rounded-md p-2 w-full mt-2"
          />
          <button
            onClick={handleServiceAdd}
            className="bg-blue-500 text-white px-4 py-2 rounded-md mt-4"
          >
            Add Service
          </button>
        </div>
      </div>

      {/* Save Changes */}
      <button
        onClick={updateMerchantProfile}
        className="bg-green-500 text-white px-4 py-2 rounded-md mt-4"
      >
        Save Changes
      </button>
    </div>
  );
};

export default MerchantDashboard;
