import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";

const CustomerDashboard = () => {
  const [services, setServices] = useState([]); // Services list
  const [query, setQuery] = useState(""); // Search query
  const [filters, setFilters] = useState({ type: "", minPrice: "", maxPrice: "" }); // Filter settings
  const [userDetails, setUserDetails] = useState({ name: "", email: "", phone: "" }); // User details (from localStorage)
  const navigate = useNavigate(); // To navigate programmatically

  useEffect(() => {
    fetchServices(); // Fetch services when component mounts or filters change
    fetchUserProfile(); // Fetch user profile details from localStorage
  }, [query, filters]);

  // Fetch services based on search and filters
  const fetchServices = async () => {
    try {
      const params = new URLSearchParams({
        query,
        type: filters.type,
        minPrice: filters.minPrice,
        maxPrice: filters.maxPrice,
      }).toString();

      const response = await axios.get(`http://localhost:5000/api/customers/services?${params}`);
      setServices(response.data);
    } catch (error) {
      console.error("Error fetching services:", error);
    }
  };

  // Fetch user profile from localStorage
  const fetchUserProfile = () => {
    const userName = localStorage.getItem("userName");
    const userEmail = localStorage.getItem("userEmail");
    const userPhone = localStorage.getItem("userPhone");

    setUserDetails({
      name: userName,
      email: userEmail,
      phone: userPhone,
    });
  };

  // Handle search query change
  const handleSearch = (e) => setQuery(e.target.value);

  // Handle slot booking for the customer
  const bookSlot = async (serviceId, slotId) => {
    try {
      const authToken = localStorage.getItem("authToken");
      await axios.post(
        `http://localhost:5000/api/services/${serviceId}/book`,
        { slotId },
        { headers: { Authorization: `Bearer ${authToken}` } }
      );
      alert("Slot booked successfully!");
      fetchServices(); // Refresh services after booking
    } catch (error) {
      console.error("Error booking slot:", error);
      alert("Failed to book slot. Please try again.");
    }
  };

  return (
    <div className="min-h-screen p-6 bg-gray-100">
      <h1 className="text-2xl font-bold mb-6">Welcome to Your Dashboard, {userDetails.name}</h1>

      {/* Customer Profile Info */}
      <div className="mb-6">
        <h2 className="text-xl font-semibold">Profile</h2>
        <p><strong>Email:</strong> {userDetails.email}</p>
        <p><strong>Phone:</strong> {userDetails.phone}</p>
      </div>

      {/* Search and Filters */}
      <div className="flex items-center mb-6">
        <input
          type="text"
          placeholder="Search services..."
          value={query}
          onChange={handleSearch}
          className="p-2 border rounded mr-4"
        />
        <select
          value={filters.type}
          onChange={(e) => setFilters((prev) => ({ ...prev, type: e.target.value }))}
          className="p-2 border rounded mr-4"
        >
          <option value="">All Types</option>
          <option value="haircut">Haircut</option>
          <option value="massage">Massage</option>
        </select>
        <input
          type="number"
          placeholder="Min Price"
          value={filters.minPrice}
          onChange={(e) => setFilters((prev) => ({ ...prev, minPrice: e.target.value }))}
          className="p-2 border rounded mr-4"
        />
        <input
          type="number"
          placeholder="Max Price"
          value={filters.maxPrice}
          onChange={(e) => setFilters((prev) => ({ ...prev, maxPrice: e.target.value }))}
          className="p-2 border rounded"
        />
      </div>

      {/* Service List */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        {services.map((service) => (
          <div key={service._id} className="bg-white p-4 rounded shadow">
            <h2 className="text-xl font-semibold">{service.title}</h2>
            <p>{service.description}</p>
            <p className="text-gray-600">
              <strong>Price:</strong> ${service.price}
            </p>
            <div className="mt-4">
              {service.slots.length > 0 ? (
                service.slots.map((slot) => (
                  <div key={slot._id} className="flex justify-between items-center mb-2">
                    <p>
                      {slot.startTime} - {slot.endTime} ({slot.recurrence})
                    </p>
                    {!slot.isBooked && (
                      <button
                        onClick={() => bookSlot(service._id, slot._id)}
                        className="bg-blue-500 text-white px-4 py-2 rounded"
                      >
                        Book Now
                      </button>
                    )}
                  </div>
                ))
              ) : (
                <p>No available slots</p>
              )}
            </div>
            <Link
              to={`/services/${service._id}`}
              className="text-blue-500 underline mt-4 block"
            >
              View Details
            </Link>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CustomerDashboard;
