import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const MerchantDashboard = () => {
  const [merchant, setMerchant] = useState(null);
  const [services, setServices] = useState([]);
  const [newService, setNewService] = useState({
    name: "",
    description: "",
    price: "",
    availability: "",
  });
  const [error, setError] = useState("");
  const navigate = useNavigate();

  // Fetch Merchant Profile
  useEffect(() => {
    const fetchMerchantData = async () => {
      const authToken = localStorage.getItem("authToken");
      if (!authToken) return navigate("/login");

      try {
        const headers = { Authorization: `Bearer ${authToken}` };
        const profileResponse = await axios.get(
          "http://localhost:5000/api/merchants/profile",
          { headers }
        );
        setMerchant(profileResponse.data);

        const servicesResponse = await axios.get(
          "http://localhost:5000/api/merchants/services",
          { headers }
        );
        setServices(servicesResponse.data.services || []);
      } catch (err) {
        setError("Error fetching data. Please try again.");
      }
    };

    fetchMerchantData();
  }, [navigate]);

  // Add New Service
  const handleAddService = async () => {
    const authToken = localStorage.getItem("authToken");
    const headers = { Authorization: `Bearer ${authToken}` };

    try {
      const response = await axios.put(
        "http://localhost:5000/api/merchants/services",
        { services: [...services, newService] },
        { headers }
      );
      setServices(response.data.services);
      setNewService({ name: "", description: "", price: "", availability: "" });
    } catch (err) {
      setError("Error adding service. Please try again.");
    }
  };

  // Logout Functionality
  const handleLogout = () => {
    localStorage.removeItem("authToken");
    navigate("/login");
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="bg-blue-600 p-4 text-white flex justify-between items-center">
        <h1 className="text-2xl font-bold">Merchant Dashboard</h1>
        <button
          onClick={handleLogout}
          className="bg-red-500 px-4 py-2 rounded text-white"
        >
          Logout
        </button>
      </div>

      <div className="container mx-auto p-4">
        {error && <p className="text-red-500">{error}</p>}

        {merchant && (
          <div className="bg-white shadow p-4 rounded-lg mb-4">
            <h2 className="text-xl font-semibold">Welcome, {merchant.name}</h2>
            <p>Email: {merchant.email}</p>
            <p>Services Managed: {services.length}</p>
          </div>
        )}

        <div className="bg-white shadow p-4 rounded-lg">
          <h2 className="text-lg font-semibold mb-2">Manage Services</h2>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
            {services.map((service, index) => (
              <div
                key={index}
                className="p-4 border rounded-lg shadow bg-gray-50"
              >
                <h3 className="font-bold">{service.name}</h3>
                <p>{service.description}</p>
                <p>Price: ${service.price}</p>
                <p>Availability: {service.availability}</p>
              </div>
            ))}
          </div>

          <div className="mt-4">
            <h3 className="text-lg font-semibold">Add New Service</h3>
            <input
              type="text"
              placeholder="Name"
              value={newService.name}
              onChange={(e) =>
                setNewService({ ...newService, name: e.target.value })
              }
              className="w-full p-2 border rounded mb-2"
            />
            <textarea
              placeholder="Description"
              value={newService.description}
              onChange={(e) =>
                setNewService({ ...newService, description: e.target.value })
              }
              className="w-full p-2 border rounded mb-2"
            ></textarea>
            <input
              type="number"
              placeholder="Price"
              value={newService.price}
              onChange={(e) =>
                setNewService({ ...newService, price: e.target.value })
              }
              className="w-full p-2 border rounded mb-2"
            />
            <input
              type="text"
              placeholder="Availability"
              value={newService.availability}
              onChange={(e) =>
                setNewService({ ...newService, availability: e.target.value })
              }
              className="w-full p-2 border rounded mb-2"
            />
            <button
              onClick={handleAddService}
              className="bg-blue-500 px-4 py-2 text-white rounded"
            >
              Add Service
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default MerchantDashboard;
