import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const MerchantDashboard = () => {
  const [profile, setProfile] = useState({});
  const [services, setServices] = useState([]);
  const [bookings, setBookings] = useState([]);
  const [newService, setNewService] = useState({
    title: "",
    description: "",
    price: "",
    slots: [],
    templateId: "",
  });
  const navigate = useNavigate();

  const authToken = localStorage.getItem("authToken");

  useEffect(() => {
    fetchProfile();
    fetchServices();
    fetchBookings();
  }, []);

  const fetchProfile = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/merchants/profile", {
        headers: { Authorization: `Bearer ${authToken}` },
      });
      setProfile(response.data);
    } catch (error) {
      console.error("Error fetching profile:", error);
    }
  };

  const fetchServices = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/merchants/services", {
        headers: { Authorization: `Bearer ${authToken}` },
      });
      setServices(response.data);
    } catch (error) {
      console.error("Error fetching services:", error);
    }
  };

  const fetchBookings = async () => {
    try {
      const response = await axios.get("http://localhost:5000/api/merchants/bookings", {
        headers: { Authorization: `Bearer ${authToken}` },
      });
      setBookings(response.data);
    } catch (error) {
      console.error("Error fetching bookings:", error);
    }
  };

  const handleAddService = async () => {
    try {
      const response = await axios.post(
        "http://localhost:5000/api/merchants/services",
        newService,
        { headers: { Authorization: `Bearer ${authToken}` } }
      );
      setServices([...services, response.data.service]);
      setNewService({ title: "", description: "", price: "", slots: [], templateId: "" });
    } catch (error) {
      console.error("Error adding service:", error);
    }
  };

  const handleLogout = () => {
    localStorage.clear();
    navigate("/login");
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <div className="bg-blue-600 text-white p-4">
        <h1 className="text-xl">Merchant Dashboard</h1>
        <button className="ml-auto bg-red-500 px-4 py-2 rounded" onClick={handleLogout}>
          Logout
        </button>
      </div>
      <div className="p-4">
        <h2 className="text-lg font-bold">Profile</h2>
        <p>Name: {profile.name}</p>
        <p>Email: {profile.email}</p>
        <p>Phone: {profile.phone}</p>
        <h2 className="text-lg font-bold mt-4">Services</h2>
        <div>
          {services.map((service) => (
            <div key={service._id} className="bg-white p-4 shadow-md rounded mb-4">
              <h3>{service.title}</h3>
              <p>{service.description}</p>
              <p>Price: {service.price}</p>
            </div>
          ))}
        </div>
        <h2 className="text-lg font-bold mt-4">Add New Service</h2>
        <div className="bg-white p-4 shadow-md rounded">
          <input
            type="text"
            placeholder="Title"
            value={newService.title}
            onChange={(e) => setNewService({ ...newService, title: e.target.value })}
          />
          <textarea
            placeholder="Description"
            value={newService.description}
            onChange={(e) => setNewService({ ...newService, description: e.target.value })}
          />
          <input
            type="number"
            placeholder="Price"
            value={newService.price}
            onChange={(e) => setNewService({ ...newService, price: e.target.value })}
          />
          <button onClick={handleAddService} className="bg-green-500 px-4 py-2 rounded">
            Add Service
          </button>
        </div>
        <h2 className="text-lg font-bold mt-4">Bookings</h2>
        <div>
          {bookings.map((booking) => (
            <div key={booking._id} className="bg-white p-4 shadow-md rounded mb-4">
              <p>Customer: {booking.customerName}</p>
              <p>Service: {booking.serviceTitle}</p>
              <p>Date: {booking.date}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default MerchantDashboard;
