import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const BASE_URL = "http://localhost:5000/api/merchants"; // Adjusted to target port 5000

const MerchantDashboard = () => {
  const [activeTab, setActiveTab] = useState("profile"); // Manage tabs
  const [loading, setLoading] = useState(true);
  const [merchant, setMerchant] = useState(null);
  const [profileData, setProfileData] = useState({});
  const [services, setServices] = useState([]);
  const [newService, setNewService] = useState({
    title: "",
    description: "",
    price: "",
    slots: [],
  });
  const navigate = useNavigate();

  const authToken = localStorage.getItem("authToken");

  useEffect(() => {
    if (!authToken) {
      navigate("/loginsignup");
    } else {
      fetchMerchantData();
    }
  }, [authToken, navigate]);

  const fetchMerchantData = async () => {
    try {
      const profileRes = await axios.get(`${BASE_URL}/profile`, {
        headers: { Authorization: authToken },
      });
      setMerchant(profileRes.data);
      setProfileData({
        businessName: profileRes.data.businessName || "",
        location: profileRes.data.location || "",
        description: profileRes.data.description || "",
        hours: profileRes.data.hours || "",
      });

      const servicesRes = await axios.get(`${BASE_URL}/services`, {
        headers: { Authorization: authToken },
      });
      setServices(servicesRes.data);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching merchant data:", error);
      setLoading(false);
    }
  };

  const handleProfileChange = (e) => {
    const { name, value } = e.target;
    setProfileData((prev) => ({ ...prev, [name]: value }));
  };

  const updateProfile = async () => {
    try {
      const response = await axios.put(
        `${BASE_URL}/profile`,
        { ...profileData },
        {
          headers: { Authorization: authToken },
        }
      );
      setMerchant(response.data);
      alert("Profile updated successfully!");
    } catch (error) {
      console.error("Error updating profile:", error);
    }
  };

  const handleServiceChange = (e) => {
    const { name, value } = e.target;
    setNewService((prev) => ({ ...prev, [name]: value }));
  };

  const addService = async () => {
    try {
      const response = await axios.post(
        `${BASE_URL}/services`,
        { ...newService },
        {
          headers: { Authorization: authToken },
        }
      );
      setServices((prev) => [...prev, response.data]);
      setNewService({
        title: "",
        description: "",
        price: "",
        slots: [],
      });
      alert("Service added successfully!");
    } catch (error) {
      console.error("Error adding service:", error);
    }
  };

  const deleteService = async (serviceId) => {
    try {
      await axios.delete(`${BASE_URL}/services/${serviceId}`, {
        headers: { Authorization: authToken },
      });
      setServices((prev) => prev.filter((service) => service._id !== serviceId));
      alert("Service deleted successfully!");
    } catch (error) {
      console.error("Error deleting service:", error);
    }
  };

  const logout = () => {
    localStorage.removeItem("authToken");
    navigate("/login");
  };

  if (loading) {
    return <div className="text-center p-4">Loading...</div>;
  }

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <div className="flex justify-between items-center mb-4">
        <h1 className="text-2xl font-bold">Merchant Dashboard</h1>
        <button
          onClick={logout}
          className="bg-red-500 text-white px-4 py-2 rounded"
        >
          Logout
        </button>
      </div>
      <div className="flex mb-6 border-b">
        <button
          onClick={() => setActiveTab("profile")}
          className={`py-2 px-4 ${
            activeTab === "profile" ? "border-b-2 border-blue-500" : ""
          }`}
        >
          Profile
        </button>
        <button
          onClick={() => setActiveTab("services")}
          className={`py-2 px-4 ${
            activeTab === "services" ? "border-b-2 border-blue-500" : ""
          }`}
        >
          Services
        </button>
        <button
          onClick={() => setActiveTab("settings")}
          className={`py-2 px-4 ${
            activeTab === "settings" ? "border-b-2 border-blue-500" : ""
          }`}
        >
          Settings
        </button>
      </div>

      {activeTab === "profile" && (
        <div className="bg-white p-4 rounded shadow">
          <h2 className="text-xl font-semibold">Profile</h2>
          <div>
            <input
              type="text"
              name="businessName"
              value={profileData.businessName}
              onChange={handleProfileChange}
              placeholder="Business Name"
              className="block w-full p-2 border rounded mb-2"
            />
            <input
              type="text"
              name="location"
              value={profileData.location}
              onChange={handleProfileChange}
              placeholder="Location"
              className="block w-full p-2 border rounded mb-2"
            />
            <textarea
              name="description"
              value={profileData.description}
              onChange={handleProfileChange}
              placeholder="Description"
              className="block w-full p-2 border rounded mb-2"
            ></textarea>
            <input
              type="text"
              name="hours"
              value={profileData.hours}
              onChange={handleProfileChange}
              placeholder="Business Hours"
              className="block w-full p-2 border rounded mb-2"
            />
            <button
              onClick={updateProfile}
              className="bg-blue-500 text-white px-4 py-2 rounded"
            >
              Save Changes
            </button>
          </div>
        </div>
      )}

      {activeTab === "services" && (
        <div className="bg-white p-4 rounded shadow">
          <h2 className="text-xl font-semibold">Services</h2>
          {services.map((service) => (
            <div key={service._id} className="p-4 border-b flex justify-between">
              <div>
                <h3 className="font-bold">{service.title}</h3>
                <p>{service.description}</p>
                <p>Price: ${service.price}</p>
              </div>
              <button
                onClick={() => deleteService(service._id)}
                className="bg-red-500 text-white px-4 py-2 rounded"
              >
                Delete
              </button>
            </div>
          ))}
          <h3 className="text-lg font-semibold mt-4">Add New Service</h3>
          <input
            type="text"
            name="title"
            value={newService.title}
            onChange={handleServiceChange}
            placeholder="Service Title"
            className="block w-full p-2 border rounded mb-2"
          />
          <textarea
            name="description"
            value={newService.description}
            onChange={handleServiceChange}
            placeholder="Service Description"
            className="block w-full p-2 border rounded mb-2"
          ></textarea>
          <input
            type="number"
            name="price"
            value={newService.price}
            onChange={handleServiceChange}
            placeholder="Service Price"
            className="block w-full p-2 border rounded mb-2"
          />
          <button
            onClick={addService}
            className="bg-green-500 text-white px-4 py-2 rounded"
          >
            Add Service
          </button>
        </div>
      )}

      {activeTab === "settings" && (
        <div className="bg-white p-4 rounded shadow">
          <h2 className="text-xl font-semibold">Settings</h2>
          <p>Coming soon...</p>
        </div>
      )}
    </div>
  );
};

export default MerchantDashboard;
