import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const LoginSignup = () => {
  const [isSignup, setIsSignup] = useState(false);
  const [formData, setFormData] = useState({
    role: "customer",
    name: "",
    email: "",
    phone: "",
    password: "",
  });
  const [errorMessage, setErrorMessage] = useState(""); // Error message state
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    const endpoint = isSignup ? "/register" : "/login";
  
    try {
      // API request to the backend
      const response = await axios.post(
        `http://localhost:5000${endpoint}`,
        isSignup
          ? {
              role: formData.role,
              name: formData.name,
              email: formData.email,
              phone: formData.phone,
              password: formData.password,
            }
          : {
              identifier: formData.email || formData.phone, // Login with email or phone
              password: formData.password,
            }
      );
  
      console.log("Response Data:", response.data);
  
      const { role, uniqueId, authToken, name, email, phone } = response.data;
  
      // Save authentication details and user details in localStorage
      localStorage.setItem("authToken", authToken);
      localStorage.setItem("userRole", role);
      localStorage.setItem("merchantId", uniqueId);
      localStorage.setItem("userName", name);
      localStorage.setItem("userEmail", email);
      localStorage.setItem("userPhone", phone);
  
      if (authToken) {
        // Navigate based on role
        if (role === "merchant") {
          navigate(`/merchant-dashboard/${uniqueId}`);
        } else if (role === "customer") {
          navigate(`/customer-dashboard/${uniqueId}`);
        } else {
          throw new Error("Invalid role detected. Please contact support.");
        }
      } else {
        alert("Authentication failed. Please try again.");
      }
    } catch (error) {
      console.error("Error during submission:", error);
      setErrorMessage(
        error.response?.data?.error || "An error occurred. Please try again."
      );
    }
  };
  

  return (
    <div className="flex items-center justify-center h-screen bg-gradient-to-r from-blue-500 to-purple-500">
      <div className="w-96 bg-white p-6 shadow-xl rounded-lg">
        <h2 className="text-3xl font-bold text-center text-blue-600">
          {isSignup ? "Sign Up" : "Log In"}
        </h2>
        {errorMessage && (
          <p className="text-red-500 text-sm mt-2 text-center">{errorMessage}</p>
        )}
        <form className="mt-4" onSubmit={handleSubmit}>
          {isSignup && (
            <>
              <label className="block text-gray-600">Name</label>
              <input
                type="text"
                className="w-full p-2 border rounded-md mt-2"
                value={formData.name}
                onChange={(e) =>
                  setFormData({ ...formData, name: e.target.value })
                }
                required
              />
              <label className="block text-gray-600 mt-4">Role</label>
              <select
                className="w-full p-2 border rounded-md mt-2"
                value={formData.role}
                onChange={(e) =>
                  setFormData({ ...formData, role: e.target.value })
                }
                required
              >
                <option value="customer">Customer</option>
                <option value="merchant">Merchant</option>
              </select>
              <label className="block text-gray-600 mt-4">Phone</label>
              <input
                type="text"
                className="w-full p-2 border rounded-md mt-2"
                value={formData.phone}
                onChange={(e) =>
                  setFormData({ ...formData, phone: e.target.value })
                }
                required
              />
            </>
          )}
          <label className="block text-gray-600 mt-4">Email</label>
          <input
            type="text"
            className="w-full p-2 border rounded-md mt-2"
            value={formData.email}
            onChange={(e) =>
              setFormData({
                ...formData,
                email: e.target.value,
              })
            }
            required
          />
          <label className="block text-gray-600 mt-4">Password</label>
          <input
            type="password"
            className="w-full p-2 border rounded-md mt-2"
            value={formData.password}
            onChange={(e) =>
              setFormData({ ...formData, password: e.target.value })
            }
            required
          />
          <button
            type="submit"
            className="w-full mt-6 bg-blue-600 text-white p-2 rounded-md"
          >
            {isSignup ? "Sign Up" : "Log In"}
          </button>
        </form>
        <p
          className="mt-4 text-center text-sm text-blue-500 cursor-pointer"
          onClick={() => setIsSignup(!isSignup)}
        >
          {isSignup
            ? "Already have an account? Log In"
            : "Don't have an account? Sign Up"}
        </p>
      </div>
    </div>
  );
};

export default LoginSignup;
