import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const LoginSignup = () => {
  const [isSignup, setIsSignup] = useState(false);
  const [formData, setFormData] = useState({
    role: "customer",
    name: "",
    emailOrPhone: "",
    password: "",
  });

  const navigate = useNavigate(); // Hook to redirect

  const handleSubmit = async (e) => {
    e.preventDefault();
    const endpoint = isSignup ? "/register" : "/login";

    try {
      const response = await axios.post(
        `https://localhost:5000${endpoint}`,
        isSignup
          ? {
              role: formData.role,
              name: formData.name,
              email: formData.emailOrPhone, // Assuming email input during signup
              phone: formData.emailOrPhone, // Assuming phone input during signup
              password: formData.password,
            }
          : {
              emailOrPhone: formData.emailOrPhone, // Unified email/phone input for login
              password: formData.password,
            }
      );

      console.log(response.data);

      // Redirect to dashboard based on role for successful login
      if (!isSignup) {
        const { role } = response.data;
        if (role === "customer") {
          navigate("/customer-dashboard");
        } else if (role === "merchant") {
          navigate("/merchant-dashboard");
        }
      } else {
        alert("Registration successful! You can now log in.");
        setIsSignup(false); // Toggle back to login after signup
      }
    } catch (error) {
      console.error("Error:", error);
      alert(error.response?.data?.error || "An error occurred. Please try again.");
    }
  };

  return (
    <div className="flex items-center justify-center h-screen bg-gray-200">
      <div className="w-96 bg-white p-6 shadow-md rounded-lg">
        <h2 className="text-2xl font-bold text-center text-blue-500">
          {isSignup ? "Sign Up" : "Log In"}
        </h2>
        <form className="mt-4" onSubmit={handleSubmit}>
          {isSignup && (
            <>
              <label className="block text-gray-600">Name</label>
              <input
                type="text"
                className="w-full p-2 border rounded-md"
                value={formData.name}
                onChange={(e) =>
                  setFormData({ ...formData, name: e.target.value })
                }
                required
              />
              <label className="block text-gray-600 mt-2">Role</label>
              <select
                className="w-full p-2 border rounded-md"
                value={formData.role}
                onChange={(e) =>
                  setFormData({ ...formData, role: e.target.value })
                }
                required
              >
                <option value="customer">Customer</option>
                <option value="merchant">Merchant</option>
              </select>
            </>
          )}
          <label className="block text-gray-600 mt-2">
            {isSignup ? "Email" : "Email or Phone"}
          </label>
          <input
            type="text"
            className="w-full p-2 border rounded-md"
            value={formData.emailOrPhone}
            onChange={(e) =>
              setFormData({ ...formData, emailOrPhone: e.target.value })
            }
            required
          />
          <label className="block text-gray-600 mt-2">Password</label>
          <input
            type="password"
            className="w-full p-2 border rounded-md"
            value={formData.password}
            onChange={(e) =>
              setFormData({ ...formData, password: e.target.value })
            }
            required
          />
          <button
            type="submit"
            className="w-full mt-4 bg-blue-500 text-white p-2 rounded-md"
          >
            {isSignup ? "Sign Up" : "Log In"}
          </button>
        </form>
        <p
          className="mt-4 text-center text-sm text-blue-500 cursor-pointer"
          onClick={() => setIsSignup(!isSignup)}
        >
          {isSignup
            ? "Already have an account? Log In"
            : "Don't have an account? Sign Up"}
        </p>
      </div>
    </div>
  );
};

export default LoginSignup;
