import React from "react";
import { Navigate, useLocation } from "react-router-dom";

const ProtectedRoute = ({ children, role }) => {
  const token = localStorage.getItem("authToken");
  const userRole = localStorage.getItem("userRole");
  const location = useLocation(); // Capture current location for redirection after login

  // Debugging logs to help trace issues
  console.log("Token:", token);
  console.log("User Role:", userRole);
  console.log("Required Role:", role);

  if (!token) {
    console.log("No token found. Redirecting to /auth");
    return <Navigate to="/auth" state={{ from: location }} replace />;
  }

  if (userRole !== role) {
    console.log(`Unauthorized access. Role '${userRole}' does not match '${role}'. Redirecting to /auth`);
    return <Navigate to="/auth" state={{ from: location }} replace />;
  }

  // If authorized, render the child component
  return children;
};

export default ProtectedRoute;
