import React, { useEffect, useState } from "react";
import axios from "axios";
import { useNavigate, useParams } from "react-router-dom";

const MerchantDashboard = () => {
  const { merchantId } = useParams(); // Get the merchantId from the URL
  const navigate = useNavigate();
  const [merchantData, setMerchantData] = useState(null);
  const [profile, setProfile] = useState({
    businessName: "",
    description: "",
    services: [],
  });
  const [isEditing, setIsEditing] = useState(false);

  // Fetch or create merchant data on mount
  useEffect(() => {
    const fetchMerchantData = async () => {
      const token = localStorage.getItem("authToken"); // Retrieve token from localStorage

      if (!token) {
        navigate("/auth"); // If token doesn't exist, navigate to login page
        return;
      }

      try {
        const response = await axios.get(
          `http://localhost:5000/api/merchant/${merchantId}`,
          {
            headers: { Authorization: `Bearer ${token}` }, // Send the token in the header
          }
        );
        setMerchantData(response.data); // Set merchant data from backend response
      } catch (error) {
        // Handle errors
        if (error.response && error.response.status === 404) {
          const createMerchantResponse = await axios.post(
            `http://localhost:5000/api/merchant`,
            {
              uniqueId: merchantId,
              name: "New Merchant", // Placeholder for merchant data
              email: "merchant@example.com", // Placeholder
              phone: "1234567890", // Placeholder
            },
            {
              headers: { Authorization: `Bearer ${token}` }, // Pass token for authentication
            }
          );
          setMerchantData(createMerchantResponse.data); // Set the newly created merchant data
        } else if (error.response && error.response.status === 401) {
          navigate("/auth"); // Unauthorized, navigate to login page
        } else {
          console.error("Error fetching merchant data:", error);
        }
      }
    };

    fetchMerchantData();
  }, [merchantId, navigate]);

  const handleLogout = () => {
    localStorage.clear(); // Clear local storage on logout
    navigate("/auth"); // Navigate to login page
  };

  const handleSave = async () => {
    if (!profile.businessName || !profile.description || !profile.services) {
      alert("Please fill in all required fields.");
      return;
    }

    const token = localStorage.getItem("authToken"); // Get the token from localStorage
    if (!token) {
      alert("Please log in again.");
      navigate("/auth"); // If no token is found, prompt user to log in
      return;
    }

    try {
      const response = await axios.put(
        `http://localhost:5000/api/merchant/${merchantId}`,
        profile,
        { headers: { Authorization: `Bearer ${token}` } }
      );
      setMerchantData(response.data); // Update the merchant data after saving
      setIsEditing(false); // Switch back to view mode
    } catch (error) {
      console.error("Error saving merchant data:", error);
      // Additional error handling based on response code
      if (error.response && error.response.status === 403) {
        alert("You are not authorized to make this request.");
      }
    }
  };

  return (
    <div className="p-8 bg-gray-100 h-screen">
      <header className="flex justify-between items-center bg-white p-4 shadow-md">
        <h1 className="text-xl font-bold">Merchant Dashboard</h1>
        <button
          onClick={handleLogout}
          className="px-4 py-2 bg-red-500 text-white rounded-md"
        >
          Logout
        </button>
      </header>
      {merchantData ? (
        <div className="mt-8">
          <h2 className="text-2xl font-bold">Welcome, {merchantData.name}</h2>
          <div className="mt-4 bg-white p-4 rounded-lg shadow-md">
            <h3 className="text-lg font-bold">Profile Details</h3>
            <p><strong>Business Name:</strong> {merchantData.businessDetails?.businessName || "Not Set"}</p>
            <p><strong>Description:</strong> {merchantData.businessDetails?.description || "Not Set"}</p>
            <button onClick={() => setIsEditing(true)} className="mt-4 px-4 py-2 bg-blue-500 text-white rounded">Edit Profile</button>
          </div>
        </div>
      ) : (
        <p>Loading profile...</p>
      )}

      {isEditing && (
        <div className="mt-8 bg-white p-4 rounded-lg shadow-md">
          <h3 className="text-lg font-bold">Edit Profile</h3>
          <input
            type="text"
            value={profile.businessName}
            onChange={(e) => setProfile({ ...profile, businessName: e.target.value })}
            placeholder="Business Name"
            className="p-2 border mt-2"
          />
          <textarea
            value={profile.description}
            onChange={(e) => setProfile({ ...profile, description: e.target.value })}
            placeholder="Description"
            className="p-2 border mt-2"
          />
          <input
            type="text"
            value={profile.services}
            onChange={(e) => setProfile({ ...profile, services: e.target.value })}
            placeholder="Services"
            className="p-2 border mt-2"
          />
          <button onClick={handleSave} className="mt-4 px-4 py-2 bg-green-500 text-white rounded">Save Changes</button>
        </div>
      )}
    </div>
  );
};

export default MerchantDashboard;
