import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";
import axios from "axios";

const ServiceDetails = () => {
  const { id } = useParams(); // Extract the service ID from the URL
  const [service, setService] = useState(null); // State for service details
  const [editMode, setEditMode] = useState(false); // Toggle for merchant edit mode
  const [updatedService, setUpdatedService] = useState({}); // Temporary state for editing
  const [newSlot, setNewSlot] = useState({ startTime: "", endTime: "" }); // State for adding slots
  const [userRole, setUserRole] = useState(""); // Customer or Merchant
  const authToken = localStorage.getItem("authToken");

  useEffect(() => {
    fetchServiceDetails();
    determineUserRole();
  }, [id]);

  const determineUserRole = () => {
    const role = localStorage.getItem("userRole"); // Role saved during login
    setUserRole(role);
  };

  const fetchServiceDetails = async () => {
    try {
      const response = await axios.get(`http://localhost:5000/api/services/${id}`, {
        headers: { Authorization: authToken },
      });
      setService(response.data);
      setUpdatedService(response.data); // Pre-fill updatedService for editing
    } catch (error) {
      console.error("Error fetching service details:", error);
    }
  };

  const handleEditChange = (e) => {
    const { name, value } = e.target;
    setUpdatedService((prev) => ({ ...prev, [name]: value }));
  };

  const saveChanges = async () => {
    try {
      const response = await axios.put(
        `http://localhost:5000/api/services/${id}`,
        updatedService,
        { headers: { Authorization: authToken } }
      );
      setService(response.data); // Update service with new changes
      setEditMode(false);
      alert("Service updated successfully!");
    } catch (error) {
      console.error("Error updating service details:", error);
    }
  };

  const addSlot = async () => {
    try {
      const response = await axios.post(
        `http://localhost:5000/api/services/${id}/slots`,
        newSlot,
        { headers: { Authorization: authToken } }
      );
      setService(response.data); // Update service with new slot
      setNewSlot({ startTime: "", endTime: "" }); // Reset the slot form
    } catch (error) {
      console.error("Error adding slot:", error);
    }
  };

  const bookSlot = async (slotId) => {
    try {
      await axios.post(
        `http://localhost:5000/api/services/${id}/book`,
        { slotId },
        { headers: { Authorization: authToken } }
      );
      alert("Slot booked successfully!");
      fetchServiceDetails(); // Refresh the page to reflect booked slot
    } catch (error) {
      console.error("Error booking slot:", error);
    }
  };

  if (!service) return <p>Loading service details...</p>;

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      <div className="bg-white p-4 rounded shadow mb-6">
        <h1 className="text-3xl font-bold">
          {editMode ? (
            <input
              type="text"
              name="title"
              value={updatedService.title}
              onChange={handleEditChange}
              className="border p-2 w-full"
            />
          ) : (
            service.title
          )}
        </h1>
        {editMode ? (
          <textarea
            name="description"
            value={updatedService.description}
            onChange={handleEditChange}
            className="border p-2 w-full mt-2"
          />
        ) : (
          <p>{service.description}</p>
        )}
        <p>
          <strong>Price:</strong>{" "}
          {editMode ? (
            <input
              type="number"
              name="price"
              value={updatedService.price}
              onChange={handleEditChange}
              className="border p-2 w-24"
            />
          ) : (
            `$${service.price}`
          )}
        </p>
        {service.merchantId && (
          <p>
            <strong>Contact:</strong> {service.merchantId?.email} |{" "}
            {service.merchantId?.phone}
          </p>
        )}
        {editMode && (
          <button
            onClick={saveChanges}
            className="bg-blue-500 text-white px-4 py-2 rounded mt-4"
          >
            Save Changes
          </button>
        )}
        {userRole === "merchant" && (
          <button
            onClick={() => setEditMode(!editMode)}
            className="bg-gray-500 text-white px-4 py-2 rounded mt-4 ml-4"
          >
            {editMode ? "Cancel" : "Edit"}
          </button>
        )}
      </div>

      <div className="bg-white p-4 rounded shadow">
        <h2 className="text-xl font-semibold">Available Slots</h2>
        <ul>
          {service.slots.map((slot, index) => (
            <li key={index} className="flex justify-between items-center">
              {slot.startTime} - {slot.endTime}{" "}
              {!slot.isBooked && userRole === "customer" && (
                <button
                  onClick={() => bookSlot(slot._id)}
                  className="bg-green-500 text-white px-4 py-2 rounded"
                >
                  Book
                </button>
              )}
            </li>
          ))}
        </ul>
        {userRole === "merchant" && (
          <div className="mt-4">
            <h3 className="text-lg font-semibold">Add Slot</h3>
            <div className="flex items-center">
              <input
                type="time"
                value={newSlot.startTime}
                onChange={(e) =>
                  setNewSlot((prev) => ({ ...prev, startTime: e.target.value }))
                }
                className="border p-2 mr-2"
              />
              <input
                type="time"
                value={newSlot.endTime}
                onChange={(e) =>
                  setNewSlot((prev) => ({ ...prev, endTime: e.target.value }))
                }
                className="border p-2 mr-2"
              />
              <button
                onClick={addSlot}
                className="bg-blue-500 text-white px-4 py-2 rounded"
              >
                Add Slot
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ServiceDetails;
