import React, { useState } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const LoginSignup = () => {
  const [isSignup, setIsSignup] = useState(false); // Toggle between signup and login
  const [formData, setFormData] = useState({
    role: "customer", // Default role for signup
    name: "",
    email: "",
    phone: "",
    emailOrPhone: "",
    password: "",
  });
  const [errorMessage, setErrorMessage] = useState(""); // To handle errors

  const navigate = useNavigate(); // Hook for redirection

  const handleSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage(""); // Clear previous errors
    const endpoint = isSignup ? "/register" : "/login";

    try {
      const response = await axios.post(
        `http://localhost:5000${endpoint}`,
        isSignup
          ? {
              role: formData.role,
              name: formData.name,
              email: formData.email,
              phone: formData.phone,
              password: formData.password,
            }
          : {
              identifier: formData.emailOrPhone,
              password: formData.password,
            }
      );

      console.log(response.data);

      if (isSignup) {
        alert("Registration successful! You can now log in.");
        setIsSignup(false); // Switch back to login view
      } else {
        const { role } = response.data;
        if (role === "customer") {
          navigate("/customer-dashboard");
        } else if (role === "merchant") {
          navigate("/merchant-dashboard");
        }
      }
    } catch (error) {
      console.error("Error:", error);
      setErrorMessage(
        error.response?.data?.error || "An error occurred. Please try again."
      );
    }
  };

  const handleInputChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
    setErrorMessage(""); // Clear error when the user starts typing
  };

  return (
    <div className="flex items-center justify-center h-screen bg-gray-200">
      <div className="w-96 bg-white p-6 shadow-md rounded-lg">
        <h2 className="text-2xl font-bold text-center text-blue-500">
          {isSignup ? "Sign Up" : "Log In"}
        </h2>
        <form className="mt-4" onSubmit={handleSubmit}>
          {isSignup && (
            <>
              <label className="block text-gray-600">Name</label>
              <input
                type="text"
                name="name"
                className="w-full p-2 border rounded-md"
                value={formData.name}
                onChange={handleInputChange}
                required
              />
              <label className="block text-gray-600 mt-2">Role</label>
              <select
                name="role"
                className="w-full p-2 border rounded-md"
                value={formData.role}
                onChange={handleInputChange}
                required
              >
                <option value="customer">Customer</option>
                <option value="merchant">Merchant</option>
              </select>
              <label className="block text-gray-600 mt-2">Email</label>
              <input
                type="email"
                name="email"
                className="w-full p-2 border rounded-md"
                value={formData.email}
                onChange={handleInputChange}
                required
              />
              <label className="block text-gray-600 mt-2">Phone</label>
              <input
                type="tel"
                name="phone"
                className="w-full p-2 border rounded-md"
                value={formData.phone}
                onChange={handleInputChange}
                required
              />
            </>
          )}
          {!isSignup && (
            <>
              <label className="block text-gray-600 mt-2">Email or Phone</label>
              <input
                type="text"
                name="emailOrPhone"
                className="w-full p-2 border rounded-md"
                value={formData.emailOrPhone}
                onChange={handleInputChange}
                required
              />
            </>
          )}
          <label className="block text-gray-600 mt-2">Password</label>
          <input
            type="password"
            name="password"
            className="w-full p-2 border rounded-md"
            value={formData.password}
            onChange={handleInputChange}
            required
          />
          {errorMessage && (
            <p className="text-red-500 text-sm mt-2">{errorMessage}</p>
          )}
          <button
            type="submit"
            className="w-full mt-4 bg-blue-500 text-white p-2 rounded-md"
          >
            {isSignup ? "Sign Up" : "Log In"}
          </button>
        </form>
        <p
          className="mt-4 text-center text-sm text-blue-500 cursor-pointer"
          onClick={() => setIsSignup(!isSignup)}
        >
          {isSignup
            ? "Already have an account? Log In"
            : "Don't have an account? Sign Up"}
        </p>
      </div>
    </div>
  );
};

export default LoginSignup;
