import React, { useState, useEffect } from "react";
import axios from "axios";
import { useNavigate } from "react-router-dom";

const MerchantDashboard = () => {
  const [profile, setProfile] = useState({});
  const [services, setServices] = useState([]);
  const [newService, setNewService] = useState({ name: "", price: "", slots: "" });
  const navigate = useNavigate();

  useEffect(() => {
    // Fetch merchant profile and user details on component load
    const fetchProfile = async () => {
      try {
        const token = localStorage.getItem("authToken");
        const headers = { Authorization: `Bearer ${token}` };

        // Fetch profile details
        const profileResponse = await axios.get("/api/merchants/profile", { headers });
        setProfile(profileResponse.data);

        // Fetch services if available
        const userResponse = await axios.get("/api/user/details", { headers });
        setProfile((prev) => ({ ...prev, ...userResponse.data }));

        if (profileResponse.data.services) {
          setServices(profileResponse.data.services);
        }
      } catch (err) {
        console.error("Error fetching profile:", err);
      }
    };

    fetchProfile();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem("authToken");
    navigate("/login");
  };

  const handleServiceAdd = async () => {
    if (!newService.name || !newService.price || !newService.slots) {
      alert("Please fill all fields");
      return;
    }

    try {
      const token = localStorage.getItem("authToken");
      const headers = { Authorization: `Bearer ${token}` };

      const updatedServices = [...services, newService];
      await axios.put("/api/merchants/services", { services: updatedServices }, { headers });

      setServices(updatedServices);
      setNewService({ name: "", price: "", slots: "" });
      alert("Service added successfully!");
    } catch (err) {
      console.error("Error adding service:", err);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100">
      <header className="bg-blue-600 text-white py-4">
        <div className="container mx-auto flex justify-between items-center px-4">
          <h1 className="text-2xl font-bold">Merchant Dashboard</h1>
          <button
            onClick={handleLogout}
            className="bg-red-500 px-4 py-2 rounded hover:bg-red-700"
          >
            Logout
          </button>
        </div>
      </header>

      <main className="container mx-auto p-4">
        {/* Profile Section */}
        <section className="bg-white shadow-md rounded p-4 mb-6">
          <h2 className="text-xl font-semibold mb-4">Profile</h2>
          <p><strong>Name:</strong> {profile.name || "N/A"}</p>
          <p><strong>Email:</strong> {profile.email || "N/A"}</p>
          <p><strong>Phone:</strong> {profile.phone || "N/A"}</p>
        </section>

        {/* Services Section */}
        <section className="bg-white shadow-md rounded p-4">
          <h2 className="text-xl font-semibold mb-4">Services</h2>
          <div className="mb-4">
            <label className="block mb-2 font-medium">Service Name</label>
            <input
              type="text"
              value={newService.name}
              onChange={(e) => setNewService({ ...newService, name: e.target.value })}
              className="w-full border rounded px-4 py-2"
            />
          </div>
          <div className="mb-4">
            <label className="block mb-2 font-medium">Price</label>
            <input
              type="number"
              value={newService.price}
              onChange={(e) => setNewService({ ...newService, price: e.target.value })}
              className="w-full border rounded px-4 py-2"
            />
          </div>
          <div className="mb-4">
            <label className="block mb-2 font-medium">Slots (comma-separated)</label>
            <input
              type="text"
              value={newService.slots}
              onChange={(e) => setNewService({ ...newService, slots: e.target.value.split(",") })}
              className="w-full border rounded px-4 py-2"
            />
          </div>
          <button
            onClick={handleServiceAdd}
            className="bg-blue-500 px-4 py-2 text-white rounded hover:bg-blue-700"
          >
            Add Service
          </button>
        </section>

        {/* Services List */}
        <section className="mt-6 bg-white shadow-md rounded p-4">
          <h2 className="text-xl font-semibold mb-4">Existing Services</h2>
          {services.length > 0 ? (
            <ul>
              {services.map((service, index) => (
                <li key={index} className="border-b py-2">
                  <strong>{service.name}</strong> - ${service.price}
                  <div>
                    Slots: {service.slots.join(", ")}
                  </div>
                </li>
              ))}
            </ul>
          ) : (
            <p>No services added yet.</p>
          )}
        </section>
      </main>
    </div>
  );
};

export default MerchantDashboard;
