import React from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Homepage from './pages/homepage';
import LoginSignup from './pages/loginsignup';
import ForbiddenPage from './pages/forbidden';    // Create this page to show when access is denied
import MerchantDashboard from './pages/merchant-dashboard';
import ServiceDetails from "./components/ServiceDetails";
import CustomerDashboard from "./pages/customer-dashboard";  // Assuming CustomerDashboard component

const App = () => {
  return (
    <Router>
      <Routes>
        {/* Route for Homepage */}
        <Route path="/" element={<Homepage />} />

        {/* Route for Login/Signup */}
        <Route path="/auth" element={<LoginSignup />} />
        <Route path="/merchant-dashboard/:uniqueId" element={<MerchantDashboard />} />
        <Route path="/customer-dashboard" element={<CustomerDashboard />} />

  <Route path="/services/:id" element={<ServiceDetails />} />
        {/* Protected Route for Merchant Dashboard */}

        {/* Forbidden page when access is denied */}
        <Route path="/forbidden" element={<ForbiddenPage />} />
      </Routes>
    </Router>
  );
};

export default App;
